function main(sys, appdata, ctx){
    var search = sys.filter("user").eq("id",sys.user().userid);
	var user = search.selectOne(true);
	if(user != null){
		ctx.set("loginname",user.get("name"));
        ctx.set("avatar",user.get("avatar"));
	}

}

